import React, { useState } from "react";
import { Chess } from "chess.js";
import { Chessboard } from "react-chessboard";

const ChessVisualizer = () => {
  const [game, setGame] = useState(new Chess());
  const [fen, setFen] = useState("start");

  // Handle move from board
  const onDrop = (sourceSquare, targetSquare) => {
    const gameCopy = new Chess(game.fen());
    const move = gameCopy.move({
      from: sourceSquare,
      to: targetSquare,
      promotion: "q",
    });

    if (move === null) return false; // illegal move
    setGame(gameCopy);
    setFen(gameCopy.fen());
    return true;
  };

  // Handle PGN upload
  const handlePGNUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      const pgn = e.target.result;
      const gameCopy = new Chess();
      const success = gameCopy.loadPgn(pgn);
      if (success) {
        setGame(gameCopy);
        setFen(gameCopy.fen());
      } else {
        alert("Invalid PGN file");
      }
    };
    reader.readAsText(file);
  };

  return (
    <div className="flex flex-col items-center">
      <h2 className="text-xl font-bold mb-4">Chess Visualizer</h2>

      <Chessboard position={fen} onPieceDrop={onDrop} boardWidth={500} />

      <input
        type="file"
        accept=".pgn"
        onChange={handlePGNUpload}
        className="mt-4"
      />
    </div>
  );
};

export default ChessVisualizer;
