import React, { useState, useEffect, useRef } from 'react';
import { Chess } from 'chess.js';
import { Chessboard } from 'react-chessboard';

const ChessVisualizer = () => {
  const [game, setGame] = useState(new Chess());
  const [pgnInput, setPgnInput] = useState('');
  const [currentMoveIndex, setCurrentMoveIndex] = useState(0);
  const [gameHistory, setGameHistory] = useState([]);
  const [gameHeaders, setGameHeaders] = useState({});
  const [isPlaying, setIsPlaying] = useState(false);
  const playInterval = useRef(null);

  // Load PGN
  const loadPgn = () => {
    if (!pgnInput.trim()) return alert('Please enter a PGN');

    const newGame = new Chess();
    const ok = newGame.loadPgn(pgnInput);

    if (!ok) return alert('Invalid PGN');

    setGame(newGame);
    setGameHeaders(extractHeaders(pgnInput));
    const history = newGame.history({ verbose: true });
    setGameHistory(history);
    setCurrentMoveIndex(history.length);
  };

  // Extract headers from PGN
  const extractHeaders = (pgn) => {
    const headers = {};
    const pgnLines = pgn.split('\n');
    pgnLines.forEach(line => {
      const match = line.match(/\[(\w+)\s+"(.+)"\]/);
      if (match) headers[match[1]] = match[2];
    });
    return headers;
  };

  // Navigate to move index
  const goToMove = (index) => {
    const newGame = new Chess();
    for (let i = 0; i < index; i++) {
      newGame.move(gameHistory[i]);
    }
    setGame(newGame);
    setCurrentMoveIndex(index);
  };

  // Step controls
  const previousMove = () => currentMoveIndex > 0 && goToMove(currentMoveIndex - 1);
  const nextMove = () => currentMoveIndex < gameHistory.length && goToMove(currentMoveIndex + 1);

  // Auto-play
  useEffect(() => {
    if (isPlaying) {
      playInterval.current = setInterval(() => {
        setCurrentMoveIndex((i) => {
          if (i < gameHistory.length) {
            goToMove(i + 1);
            return i + 1;
          } else {
            setIsPlaying(false);
            clearInterval(playInterval.current);
            return i;
          }
        });
      }, 1000);
    } else {
      clearInterval(playInterval.current);
    }
    return () => clearInterval(playInterval.current);
  }, [isPlaying, gameHistory]);

  // React-chessboard move handling
  const onDrop = (source, target) => {
    const move = game.move({ from: source, to: target, promotion: 'q' });
    if (move) {
      setGame(new Chess(game.fen()));
      setGameHistory(game.history({ verbose: true }));
      setCurrentMoveIndex(game.history().length);
      return true;
    }
    return false;
  };

  return (
    <div className="max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold text-center mb-8">Chess Game Visualizer</h1>

      <div className="grid md:grid-cols-2 gap-8">
        {/* Board */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <Chessboard
            position={game.fen()}
            onPieceDrop={onDrop}
            boardWidth={400}
          />

          {/* Controls */}
          <div className="flex justify-center space-x-2 mt-4">
            <button onClick={() => goToMove(0)} disabled={currentMoveIndex === 0}>⏮️</button>
            <button onClick={previousMove} disabled={currentMoveIndex === 0}>⏪</button>
            <button onClick={() => setIsPlaying(!isPlaying)} disabled={gameHistory.length === 0}>
              {isPlaying ? '⏸️ Pause' : '▶️ Play'}
            </button>
            <button onClick={nextMove} disabled={currentMoveIndex >= gameHistory.length}>⏩</button>
            <button onClick={() => goToMove(gameHistory.length)} disabled={currentMoveIndex >= gameHistory.length}>⏭️</button>
          </div>
        </div>

        {/* PGN Input + Info */}
        <div>
          <textarea
            value={pgnInput}
            onChange={(e) => setPgnInput(e.target.value)}
            placeholder="Paste PGN here..."
            className="w-full h-40 border p-2"
          />
          <button onClick={loadPgn} className="bg-blue-500 text-white px-4 py-2 mt-2">Load PGN</button>

          {Object.keys(gameHeaders).length > 0 && (
            <div className="mt-4">
              <h3 className="font-bold">Game Info</h3>
              {Object.entries(gameHeaders).map(([k, v]) => (
                <p key={k}><b>{k}:</b> {v}</p>
              ))}
            </div>
          )}

          {gameHistory.length > 0 && (
            <div className="mt-4">
              <h3 className="font-bold">Moves</h3>
              <div className="flex flex-wrap">
                {gameHistory.map((move, i) => (
                  <button
                    key={i}
                    onClick={() => goToMove(i + 1)}
                    className={`m-1 px-2 py-1 border rounded ${i + 1 === currentMoveIndex ? 'bg-blue-200' : ''}`}
                  >
                    {i % 2 === 0 ? `${Math.floor(i / 2) + 1}.` : ''} {move.san}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ChessVisualizer;
